CREATE TABLE CODE_LIST ( ID INTEGER PRIMARY KEY AUTOINCREMENT , ICON TEXT, NAME TEXT, SOURCE TEXT, VALUE TEXT, VALUE_SK TEXT, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE HABITAT ( ID INTEGER PRIMARY KEY AUTOINCREMENT , FOREST_AGE TEXT, FOREST_TYPE TEXT, TRACK TEXT, TREE_TYPE TEXT, TYPE TEXT, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE PHOTO ( ID INTEGER PRIMARY KEY AUTOINCREMENT , ENTITY_ID INTEGER NOT NULL, ENTITY_NAME NOT NULL, FILE_NAME TEXT NOT NULL, THUMBNAIL TEXT, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE SAMPLE ( ID INTEGER PRIMARY KEY AUTOINCREMENT , SAMPLE_NUMBER TEXT NOT NULL, SAMPLE_SEQUENCE_NUMBER INTEGER, SAMPLE_TYPE TEXT, TRANSECT_FINDING_ID INTEGER NOT NULL, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE SETTINGS ( ID INTEGER PRIMARY KEY AUTOINCREMENT , LOCATION_FORMAT TEXT, TRACKER_NAME TEXT, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE TRANSECT ( ID INTEGER PRIMARY KEY AUTOINCREMENT , END_DATE_TIME INTEGER NULL, END_ELEVATION FLOAT, END_LATITUDE FLOAT, END_LONGITUDE FLOAT, FINDINGS , LOCALISATION TEXT, ROOT_DIRECTORY_NAME TEXT, ROUTE_NAME TEXT, SQUARE INTEGER, START_DATE_TIME INTEGER NULL, START_ELEVATION FLOAT, START_LATITUDE FLOAT, START_LONGITUDE FLOAT, WEATHER_ID INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE TRANSECT_FINDING_FECES ( ID INTEGER PRIMARY KEY AUTOINCREMENT , AGE TEXT, COLLECTED INTEGER, CONFIDENCE TEXT, PREY TEXT, STATE TEXT, SUBSTRACT TEXT, TRANSECT_FINDING_ID INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE TRANSECT_FINDING_FOOTPRINTS ( ID INTEGER PRIMARY KEY AUTOINCREMENT , AGE TEXT, BACK_LENGTH FLOAT, BACK_WIDHT FLOAT, CONFIDENCE TEXT, DIRECTION TEXT, FRONT_LENGTH FLOAT, FRONT_WIDHT FLOAT, GROUP_VALUE TEXT, NUMBER_OF_ANIMALS INTEGER, STRIDE FLOAT, SUBSTRACT TEXT, TRANSECT_FINDING_ID INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE TRANSECT_FINDING_OTHER ( ID INTEGER PRIMARY KEY AUTOINCREMENT , AGE TEXT, CONFIDENCE TEXT, EVIDENCE TEXT, OBSERVATIONS TEXT, SUBSTRACT TEXT, TRANSECT_FINDING_ID INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE TRANSECT_FINDING_SITE ( ID INTEGER PRIMARY KEY AUTOINCREMENT , BEFORE_AFTER_RECENT_SNOW TEXT, HABITAT_ID INTEGER, LOCATION_ELEVATION FLOAT, LOCATION_LATITUDE FLOAT, LOCATION_LONGITUDE FLOAT, SPECIES TEXT, TRANSECT_ID INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
CREATE TABLE WEATHER ( ID INTEGER PRIMARY KEY AUTOINCREMENT , HUMIDITY INTEGER, LAST_TIME_SNOWED_RAINED INTEGER, NEW_SNOW_DEPTH INTEGER, RAIN_INTENSITY INTEGER, SNOW_DEPTH INTEGER, SUNSHINE_INTENSITY INTEGER, VISIBILITY INTEGER, WIND_INTENSITY INTEGER, COMMENT TEXT, CREATED INTEGER NULL );
